mapper.update:
update wms_material_stock
set curr_stock = curr_stock + #{stockChgAmt},
gmt_modified = now()

	,modifier = #{operator}

where material_code = #{materialCode}
and cabin_code in (#{cabinCode})

mapper.select:
select * from wms_material_stock
where cabin_code = #{cabinCode}
  and status = '2'
